class Hamburger{constructor(){this.hamburgerDiv=document.querySelector("#icon"),this.divs=document.querySelector("#divTop"),this.divHead=document.querySelector(".head"),this.menuMobile=document.querySelector("#menuMobile"),this.hamburgerDiv.addEventListener("click",this.abrirMenu.bind(this)),document.addEventListener("scroll",this.animationHamburguer.bind(this))}abrirMenu(){this.divs.classList.toggle("open"),this.menuMobile.classList.toggle("active")}animationHamburguer(e){e.target.scrollingElement.scrollTop>0?(this.divHead.classList.add("animate"),this.menuMobile.classList.remove("active"),this.divs.classList.remove("open")):this.divHead.classList.remove("animate")}}class Main{constructor(){this.dropDown=document.querySelectorAll("h2"),this.nodoSection1=document.querySelector("#moveIt1"),this.nodoSection2=document.querySelector("#moveIt2"),this.nodoSection3=document.querySelector("#moveIt3"),this.dropDown.forEach(e=>{e.addEventListener("click",this.desplegar.bind(this))}),document.addEventListener("scroll",this.isScrolledIntoView.bind(this))}desplegar(e){let t=e.target.nextElementSibling;t.classList.toggle("hide"),t.classList.contains("hide")?t.classList.add("hide"):t.classList.remove("hide")}isScrolledIntoView(e){console.log("entra!");let t=this.nodoSection1.getBoundingClientRect(),i=t.top,o=t.bottom;i>=0&&o<=window.innerHeight?(console.log("se ve el elemento entero!"),this.nodoSection1.classList.remove("floatItSelfBack"),this.nodoSection1.classList.add("floatItSelf")):(this.nodoSection1.classList.remove("floatItSelf"),this.nodoSection1.classList.add("floatItSelfBack"))}}class Index{constructor(){new Hamburger,new Main}}document.addEventListener("DOMContentLoaded",()=>{new Index});