class Hamburger{constructor(){this.hamburgerDiv=document.querySelector("#icon"),this.divs=document.querySelector("#divTop"),this.divHead=document.querySelector(".head"),this.menuMobile=document.querySelector("#menuMobile"),this.hamburgerDiv.addEventListener("click",this.abrirMenu.bind(this)),document.addEventListener("scroll",this.animationHamburguer.bind(this))}abrirMenu(){this.divs.classList.toggle("open"),this.menuMobile.classList.toggle("active")}animationHamburguer(e){e.target.scrollingElement.scrollTop>0?(this.divHead.classList.add("animate"),this.menuMobile.classList.remove("active"),this.divs.classList.remove("open")):this.divHead.classList.remove("animate")}}class Main{constructor(){this.dropDown=document.querySelectorAll("h2"),this.dropDown.forEach(e=>{e.addEventListener("click",this.desplegar.bind(this))})}desplegar(e){console.log("click en section!");let t=e.target.nextElementSibling;t.classList.toggle("hide"),t.classList.contains("hide")?(console.log("Cierrate"),t.classList.add("hide")):(console.log("Abrete"),t.classList.remove("hide"))}}class Index{constructor(){new Hamburger,new Main}}document.addEventListener("DOMContentLoaded",()=>{new Index});