class Hamburger{constructor(){this.hamburgerDiv=document.querySelector("#icon"),this.divs=document.querySelector("#divTop"),this.divHead=document.querySelector(".head"),this.menuMobile=document.querySelector("#menuMobile"),this.hamburgerDiv.addEventListener("click",this.abrirMenu.bind(this)),document.addEventListener("scroll",this.animationHamburguer.bind(this))}abrirMenu(){this.divs.classList.toggle("open"),this.menuMobile.classList.toggle("active")}animationHamburguer(e){e.target.scrollingElement.scrollTop>0?(this.divHead.classList.add("animate"),this.menuMobile.classList.remove("active"),this.divs.classList.remove("open")):this.divHead.classList.remove("animate")}}class Main{constructor(){this.dropDown=document.querySelectorAll("h2"),this.nodosArrow=document.querySelectorAll(".arrow"),this.nodoSection=document.querySelectorAll(".moveIt"),this.dropDown.forEach(e=>{e.addEventListener("click",this.desplegar.bind(this))}),this.nodosArrow.forEach(e=>{e.addEventListener("click",this.desplegar.bind(this))}),document.addEventListener("scroll",this.animarSection.bind(this))}desplegar(e){let t=e.target.nextElementSibling;t.classList.toggle("hide"),t.classList.contains("hide")?(t.classList.add("hide"),this.nodosArrow.forEach(e=>{e.classList.remove("fa-caret-up"),e.classList.add("fa-caret-down")})):(t.classList.remove("hide"),this.nodosArrow.forEach(e=>{console.log("arrow"),e.classList.remove("fa-caret-down"),e.classList.add("fa-caret-up")}))}animarSection(){this.nodoSection.forEach(e=>{this.isScrolledIntoView(e)?(e.classList.remove("floatItSelfBack"),e.classList.add("floatItSelf")):(e.classList.remove("floatItSelf"),e.classList.add("floatItSelfBack"))})}isScrolledIntoView(e){let t=e.getBoundingClientRect(),s=t.top,i=t.bottom;return s>=0&&i<=window.innerHeight}}class Index{constructor(){new Hamburger,new Main}}document.addEventListener("DOMContentLoaded",()=>{new Index});